CREATE VIEW public.acorn_exam_results AS
 SELECT id,
    name,
    student_id,
    academic_year_id,
    exam_id,
    course_material_id,
    course_id,
    material_id,
    calculation_id,
    calculation_type_id,
    project_id,
    interview_id,
    expression,
    minimum,
    maximum,
    required,
    expression_type,
    needs_evaluate,
    resolved_expression,
    result,
    ((result >= COALESCE(minimum, (0)::double precision)) AND (result <= COALESCE(maximum, (100000)::double precision))) AS passed,
    to_json(explanation) AS explanation
   FROM public.acorn_exam_result_internal2s;


