		else
			delete from public.acorn_enrollment_students where enrollment_id = p_model_id and not manual_added;
			p_result := 'All bulk added Students and courses removed';
		end if;
	else
		p_result := 'Not confirmed';
	end if;

	return p_result;
end;
$$;


